<?phpclass Model {    use \Iris\BaseModel;    protected $fields = array(        "Id" => array("type" => "string"),        "Status" => array("type" => "\Iris\Status")    );    public function __construct($data) {        $this->set_data($data);    }}class BaseModelTest extends PHPUnit_Framework_TestCase {	public function setUp() {        $this->model = new Model(array("Id" => "123", "Status" => array("Code" => "0", "Description" => "Empty")));    }    public function testContent() {        $this->assertEquals("123", $this->model->Id);        $this->assertEquals("0", $this->model->Status->Code);        $this->model->set_data(array("Id" => "222", "Status" => array("Code" => "200", "Description" => "Hello")));        $this->assertEquals("222", $this->model->Id);        $this->assertEquals("200", $this->model->Status->Code);    }}